import React, { useState } from "react";

const Form = () => {

  const [name, setName] = useState('');
  const [age, setAge] = useState(0);
  const [city, setCity] = useState('');
  const [module, setModule] = useState('fund');

  return (
    <form>
      <input type="text" placeholder="Nome" value={name} />
      <input type="Number" placeholder="Idade" value={age} />
      <input type="text" placeholder="Cidade" value={city} />
      <fieldset>
        <legend>Módulo que está estudando</legend>
        <label htmlFor="fund">
          <input type="radio" name="modulo" id="fund" value="fund" checked={ module === 'fund'} />
          Fundamentos
        </label>
        <label htmlFor="front">
          <input type="radio" name="modulo" id="front" value="front" checked={ module === 'front'} />
          Front-End
        </label>
        <label htmlFor="back">
          <input type="radio" name="modulo" id="back" value="back" checked={ module === 'back'} />
          Back-End
        </label>
        <label htmlFor="sc">
          <input type="radio" name="modulo" id="sc" value="sc" checked={ module === 'sc'} />
          Ciência da Computação
        </label>
      </fieldset>
      <button type="submit">Enviar</button>
    </form>
  );
};

export default Form;
